import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
// import {createUserWithEmailAndPassword, getAuth} from "firebase/auth"

// import {getAuth} from "firebase/auth"

// const firebaseConfig = {
//   apiKey: "AIzaSyA8D4V7Fyq5WCYK2ConsRI4-NepPuRPueA",
//   authDomain: "validation-2872e.firebaseapp.com",
//   projectId: "validation-2872e",
//   storageBucket: "validation-2872e.appspot.com",
//   messagingSenderId: "360698244234",
//   appId: "1:360698244234:web:e87b7f50ab35fcd3e2d173",
//   measurementId: "G-D72ZXE4J9G"
// };

function EmpForm(){
  const [id, setId] = useState("")
  const [name, setName] = useState("")
  const [city, setCity] = useState("")
  const [mobile, setMobile] = useState("")
  const navigate = useNavigate()

  // const firebaseConfig = {
  //   apiKey: "AIzaSyA8D4V7Fyq5WCYK2ConsRI4-NepPuRPueA",
  //   authDomain: "validation-2872e.firebaseapp.com",
  //   projectId: "validation-2872e",
  //   storageBucket: "validation-2872e.appspot.com",
  //   messagingSenderId: "360698244234",
  //   appId: "1:360698244234:web:e87b7f50ab35fcd3e2d173",
  //   measurementId: "G-D72ZXE4J9G"
  // };

  const sendData = (e) => {
    e.preventDefault()
    const data = { id, name, city, mobile }
    // fetch("http://localhost:3006/Employee",{
      fetch("https://emp-reg-doc.onrender.com/Employee",{
      method: "POST",
      headers: { "Content-type": "application/json" },
      body: JSON.stringify(data)
    })
      .then(() => {
        alert("Data saved..!")
        navigate("/")
      })
      .catch((err) => {
        alert("error" + err)
      })
  }


  //  // Initialize Firebase
  //  const app = initializeApp(firebaseConfig);
  //  const auth=getAuth()

  //  const store=(e)=>{
  //    e.preventDefault()
  //  let obj={
  //    a:email,
  //    b:pswd
  //  }
  //  createUserWithEmailAndPassword(auth,obj.a,obj.b)
  //  .then(()=>{
  //    alert("Saved")
  //  }).catch((err)=>{
  //   alert(err)
  //  })
  // }

  return (
    <div className="container">
      <h2>ADD NEW EMPLOYEE DATA</h2>
      <form onSubmit={sendData}>
        <div className="mb-3" style={{ width: "400px" }}>
          <label className="form-label">ID</label>
          <input value={id} type="text" onChange={e => setId(e.target.value)} disabled="disabled" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
        </div>
        <div className="mb-3">
          <label className="form-label">Name</label>
          <input value={name} type="text" onChange={e => setName(e.target.value)} className="form-control" id="exampleInputPassword1" />
        </div>
        <div className="mb-3">
          <label className="form-label">City</label>
          <input value={city}
            type="text" onChange={e => setCity(e.target.value)} className="form-control" id="exampleInputPassword1" />
        </div>
        <div className="mb-3">
          <label className="form-label">Mobile</label>
          <input value={mobile} type="number" onChange={e => setMobile(e.target.value)} className="form-control" id="exampleInputPassword1" />
        </div>

        <button type="submit" className="btn btn-primary">Submit</button>
        <Link to="/emplist" className="btn btn-danger">Back</Link>

      </form>
    </div>
  )
}
export default EmpForm;
